import React ,{useState,useEffect}from 'react'
import axios from 'axios';
import {useParams} from "react-router-dom";

function GithubUserDetails() {
  const [user,setUser] =useState({});
  const {id}=useParams();

useEffect(() => {
   axios.get(`https://api.github.com/users/${id}`)
    .then((res) => {
        setUser(res.data)
    }).catch(err=>{
console.log(err);
  })

})


  return (

<section className='bg-gray-700 '>
      <div className="container grid place-items-center mx-auto w-96 px-6 py-20">
        <img className='rounded-t-lg w-full h-90 object-cover' src={user.avatar_url} alt="" />
        <h3 className="mb-6 text-4xl font-bold text-center text-white">
         <span className='text-gray-400'>Name:</span> {user.name}
        </h3>
        <h4 className="my-4 text-2xl text-white">
        <span className='text-gray-400'>Type:</span> {user.type}
        </h4>
       {
        user.loaction!==null?<h4 className="my-4 text-2xl text-white">
          <span className='text-gray-400'>Location:</span>  {user.location}
        </h4>
        :
        <h4 className="my-4 text-2xl text-white">
          <span className='text-gray-400'>Location: Location not specified</span> 
        </h4>
       
        }
       

        <a href={user.repos_url}        
        className="bg-white font-bold rounded-full mt-6 py-4 px-8 shadow-lg uppercase tracking-wider"
        >
          Discover Repository
        </a>
      </div>
    </section>

  )
}

export default GithubUserDetails;
