import axios from 'axios';
import React, { useEffect, useState } from 'react';

function PhotoList() {
const [users, setUsers] = useState([]);

useEffect(() => {
 axios.get('https://api.github.com/users' )
    .then(res => {
        console.log(res);
        setUsers(res.data);

    }).catch(err=>{
console.log(err);
    })

},[])



  return (
    <div>  <h1 className='m-5 text-center text-5xl capitalize ' >github users list</h1>
    <div className='grid grid-cols-3 gap-4 m-4'>  
{
            users.map((user) => {const {id, login,repos_url ,avatar_url,  html_url} = user;
                return(
                    <div key={id} className="max-w-sm bg-white rounded-lg border border-gray-200 shadow-md dark:bg-gray-800 dark:border-gray-700">
                <a href={avatar_url}>
                    <img className="rounded-t-lg w-full h-90 object-cover" src={avatar_url} alt="User profile photo" />
                </a>
                         
            <div class="p-5">
               
                    <h5 className="mb-2 text-2xl font-bold tracking-tight text-gray-900 dark:text-white">{login}</h5>
                    <a href={html_url}>
                <p className="mb-3 font-normal text-gray-700 dark:text-gray-400">visit profile</p>
                </a>
                <a href={repos_url} className=" inline-flex items-center py-2 px-3 text-sm font-medium text-center text-white bg-purple-700 rounded-lg hover:bg-indigo-900 focus:ring-4 focus:outline-none">
                    Read more
                    <svg aria-hidden="true" className="ml-2 -mr-1 w-4 h-4" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg"><path fill-rule="evenodd" d="M10.293 3.293a1 1 0 011.414 0l6 6a1 1 0 010 1.414l-6 6a1 1 0 01-1.414-1.414L14.586 11H3a1 1 0 110-2h11.586l-4.293-4.293a1 1 0 010-1.414z" clip-rule="evenodd"></path></svg>
                </a>
                  </div>
            </div>
                )}
            )}

</div>
</div>

)}
            
export default PhotoList;
