import React ,{useState,useEffect}from 'react'
import axios from 'axios';
import {useParams} from "react-router-dom";

function GithubUserDetails() {
  const [user,setUser] =useState({});
  const {id}=useParams();

useEffect(() => {
   axios.get(`https://api.github.com/users/${id}`)
    .then((res) => {
        setUser(res.data)
    }).catch(err=>{
console.log(err);
  })

})


  return (

<section className='bg-gray-600 '>
      <div className="container grid place-items-center mx-auto w-96 px-6 py-20">
        <img className='rounded-t-lg w-full h-90 object-cover' src={user.avatar_url} alt="" />
        <h2 className="mb-6 text-4xl font-bold text-center text-white">
          {user.name}
        </h2>
        <h3 className="my-4 text-2xl text-white">
          {user.url}
        </h3>
        <a href={user.repos_url}        
        className="bg-white font-bold rounded-full mt-6 py-4 px-8 shadow-lg uppercase tracking-wider"
        >
          Discover <Repository></Repository>
        </a>
      </div>
    </section>

  )
}

export default GithubUserDetails;
